-- 1-N veza
create table Genre (
	Id int primary key identity,
	[Name] nvarchar(50) not null
)
go

create table Book (
	Id int primary key identity,
	ISBN nvarchar(17) not null,
	[Name] nvarchar(255) not null,
	Author nvarchar(255) not null,
	[Description] nvarchar(max),
	PublicationDate date,
	GenreId int foreign key references Genre(Id) not null,
)
go

create table [Location] (
	Id int primary key identity,
	[Name] nvarchar(50) not null,
)
go

create table [User] (
	Id int primary key identity,
	Username nvarchar(31) not null,
	PwdHash nvarchar(255) not null, -- Salt also stored
    Role nvarchar(50) not null default 'User',
	Email nvarchar(255),
	FirstName nvarchar(50),
	LastName nvarchar(50),
	Phone nvarchar(50)
)
go

-- M-N veza
create table BookLocation (
	Id int primary key identity,
	BookId int foreign key references Book(Id) not null,
	LocationId int foreign key references [Location](Id) not null,
)
go

-- M-N veza
create table UserReservation (
	Id int primary key identity,
	[Status] int default 0 not null,
	[Date] datetime default getdate(),
	UserId int foreign key references [User](Id) not null,
	BookId int foreign key references Book(Id) not null
)
go

-- M-N veza
create table UserReview (
	Id int primary key identity,
	Rating int check (Rating >= 1 and Rating <= 5) not null,
	[Text] nvarchar(max),
	BookId int foreign key references Book(Id) not null,
	UserId int foreign key references [User](Id) not null
)
go

create table BookLog (
	Id int primary key identity,
	[Timestamp] datetime default getdate() not null,
	[Level] int not null,
	[Message] nvarchar(max) not null,
)
go

